static const struct kvalid cookies[COOKIE__MAX] = {
  { kvalid_stringne, "string" }, /* COOKIE_STRING */
  { kvalid_int, "integer" }, /* COOKIE_INTEGER */
};

static enum khttp sanitise(const struct kreq *r) {
  if (KMIME_TEXT_HTML != r->mime)
    return(KHTTP_404);
  else if (KMETHOD_GET != r->method)
    return(KHTTP_405);
  else
    return(KHTTP_200);
}

static void process(struct kreq *r) {
  char buf[32];			 
  khttp_head(r, kresps[KRESP_STATUS],
    "%s", khttps[KHTTP_200]);
  khttp_head(r, kresps[KRESP_CONTENT_TYPE],
    "%s", kmimetypes[r->mime]);
  if (NULL == r->cookiemap[COOKIE_STRING])
    khttp_head(r, kresps[KRESP_SET_COOKIE],
      "%s=%s; Path=/; expires=%s", 
      cookies[COOKIE_STRING].name, 
      "Hello, world!",
      kutil_epoch2str(time(NULL) + 60 * 60, 
        buf, sizeof(buf)));
  khttp_body(r);
  khttp_puts(r, 
    "<!DOCTYPE html>"
    "<title>Foo</title>");
  if (NULL != r->cookiemap[COOKIE_STRING])
    khttp_puts(r, "Cookie found!");
  else
    khttp_puts(r, "Cookie set.");
}

int main(void) {
  struct kreq r;
  enum khttp er;
  if (KCGI_OK != khttp_parse(&r, cookies, COOKIE__MAX, NULL, 0, 0))  
    return(EXIT_FAILURE);
  if (KHTTP_200 != (er = sanitise(&r))) {
    khttp_head(&r, kresps[KRESP_STATUS],
      "%s", khttps[er]);
    khttp_head(&r, kresps[KRESP_CONTENT_TYPE],
      "%s", kmimetypes[r.mime]);
    khttp_body(&r);
    if (KMIME_TEXT_HTML == r.mime)
      khttp_puts(&r, "Could not service request.");
  } else
    process(&r);
  khttp_free(&r);
  return(EXIT_SUCCESS);
};
