#include <sys/types.h> /* size_t, ssize_t */
#include <stdarg.h> /* va_list */
#include <stddef.h> /* NULL */
#include <stdint.h> /* int64_t */
#include <unistd.h> /* pledge */
#include <kcgi.h>

int 
main(void) {
    struct kreq r;
    const char *const pages[1] = { "index" };

    if (-1 == pledge("stdio proc", NULL))
        return 0;
    if (KCGI_OK != khttp_parse(&r, NULL, 0, pages, 1, 0))
        return 0;
    if (-1 == pledge("stdio", NULL))
        return 0;
    khttp_head(&r, kresps[KRESP_STATUS], 
        "%s", khttps[KHTTP_200]);
    khttp_head(&r, kresps[KRESP_CONTENT_TYPE], 
        "%s", kmimetypes[KMIME_TEXT_PLAIN]);
    khttp_body(&r);
    khttp_puts(&r, "Hello, world!\n");
    khttp_free(&r);
    return 0;
}

int 
main(void) {
    struct kreq r;
    const char *const pages[1] = { "index" };
    struct kfcgi *fcgi;
    enum kcgi_err er;

    if (KCGI_OK != khttp_fcgi_init(&fcgi, NULL, 0, pages, 1, 0))
        return 0;
    for (;;) {
        if (KCGI_OK != khttp_fcgi_parse(fcgi, &req))
            break;
        khttp_head(&r, kresps[KRESP_STATUS], 
            "%s", khttps[KHTTP_200]);
        khttp_head(&r, kresps[KRESP_CONTENT_TYPE], 
            "%s", kmimetypes[KMIME_TEXT_PLAIN]);
        khttp_body(&r);
        khttp_puts(&r, "Hello, world!\n");
        khttp_free(&r);
    }
    return 0;
}

int 
main(void) {
    struct kreq r;
    const char *const pages[1] = { "index" };
    struct kfcgi *fcgi;
    enum kcgi_err er;

    if (-1 == pledge("unix sendfd recvfd proc stdio", NULL))
        return 0;
    if (KCGI_OK != khttp_fcgi_init(&fcgi, NULL, 0, pages, 1, 0))
        return 0;
    if (-1 == pledge("stdio recvfd", NULL))
        return 0;
    for (;;) {
        if (KCGI_OK != khttp_fcgi_parse(fcgi, &req))
            break;
        khttp_head(&r, kresps[KRESP_STATUS], 
            "%s", khttps[KHTTP_200]);
        khttp_head(&r, kresps[KRESP_CONTENT_TYPE], 
            "%s", kmimetypes[KMIME_TEXT_PLAIN]);
        khttp_body(&r);
        khttp_puts(&r, "Hello, world!\n");
        khttp_free(&r);
    }
    khttp_fcgi_free(fcgi);
    return 0;
}
